	public List<MediaInfoDTO> searchAllMediaInfo()
	{
		final List<MediaInfoDTO> temp = new ArrayList<MediaInfoDTO>();

		JdbcTemplate jt = getJdbcTemplate();
		String sql = "SELECT * FROM media_info_tb";
		
		
		jt.query(sql,new RowCallbackHandler() 
		{
			@Override
			public void processRow(ResultSet rs) throws SQLException 
			{
				//System.out.println(rs.getString("alarm_kind_code"));
				// TODO Auto-generated method stub
				while(true)
				{
					//SimpleDateFormat format = new SimpleDateFormat("yyyy/MM/dd");
					//String date= format.format(newArrivalDate.getTime()); // 20090529
					
					MediaInfoDTO tmp = new MediaInfoDTO();
					tmp.setMediaCode(rs.getString("media_code"));
					tmp.setMemberCode(rs.getString("member_code"));
					tmp.setFileCode(rs.getString("file_code"));
					tmp.setMediaSequence(rs.getInt("media_sequence"));
					temp.add(tmp);
					
					if(rs.next()==false)
					{
						break;
					}
				}
			}
		});
		return temp;
	}
	
