	public boolean insert(String memberCode, String fileCode, int mediaSequence)
	{
		JdbcTemplate jt = getJdbcTemplate();
		String sql = "INSERT INTO media_info_tb" 
					+" VALUES ( 'mi' || media_info_sequence.nextval ,"
					+"'" + memberCode + "',"
					+"'" + fileCode + "',"
					+ mediaSequence+ ")";

		int res = jt.update(sql);

		if(res>0)
		{
			return true;
		}
		else
		{
			return false;
		}
	}
