	public List<SharedFolderDTO> searchAllSharedFolder()
	{
		final List<SharedFolderDTO> temp = new ArrayList<SharedFolderDTO>();
 
		
		JdbcTemplate jdbcTemplate = getJdbcTemplate();
		String sql = "SELECT * FROM shared_folder_tb";
		jdbcTemplate.query(sql,new RowCallbackHandler(){
			@Override
			public void processRow(ResultSet rs) throws SQLException {
				// TODO Auto-generated method stub
				while(true)
				{
					Date date = rs.getDate("shared_folder_makedate");
					
					long time = date.getTime();
					java.util.Date d = new java.util.Date(time);
					
					GregorianCalendar gc = new GregorianCalendar();
					gc.setTime(d);
					
					SharedFolderDTO tmp = new SharedFolderDTO();
					tmp.setFolderCode(rs.getString("folder_code"));
					tmp.setSharedFolderCode(rs.getString("shared_folder_code"));
					tmp.setSharedFolderMakeDate(gc);
					temp.add(tmp);
					if(rs.next() == false)
						break;
				}
			}
			
		});		
		return temp;
	}
	
