	public int updateSharedFolderMakeDate(String sharedFolderCode, GregorianCalendar makeDate)
	{
		JdbcTemplate jdbcTemplate = getJdbcTemplate();
		
		SimpleDateFormat format = new SimpleDateFormat("yyyy/MM/dd");
		
		String date= format.format(makeDate.getTime()); // 20090529
		
		
		
		final String sql = "UPDATE shared_folder_tb" 
		+" SET shared_folder_makedate="
		+"\'" + date + "\'"
		+" WHERE shared_folder_code ="
		+"\'" + sharedFolderCode + "\'";

		int res = jdbcTemplate.update(sql);
		
		return res;
	}
	
